import { Component, ElementRef, ViewChild } from '@angular/core';
import { AngularFirestore, AngularFirestoreDocument } from '@angular/fire/compat/firestore';
import { Observable } from 'rxjs';

export interface Contact {
  title : String,
  description : String
}

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.less']
})
export class ContactComponent {
  private contactCol: AngularFirestoreDocument | undefined;
  missingTitle : Boolean = false;
  missingDesc : Boolean = false;
  isOk : Boolean = false;
  @ViewChild('title') title: ElementRef | undefined
  @ViewChild('desc') desc: ElementRef | undefined

  constructor(private afs: AngularFirestore){
    this.contactCol = afs.collection('contact')
  }

  ngOnInit(): void {
  }

  post() {
    const title = this.title?.nativeElement.value;
    const desc = this.title?.nativeElement.value;
    const date = new Date
    this.contactCol?.set({"title": title, "description": desc})

    // if (title && desc) {
    //   this.isOk = true;
    // }
    // else {
    //   this.isOk = false;
    // }
  }
}

import { Component } from '@angular/core';
import { AngularFirestore, AngularFirestoreCollection } from '@angular/fire/compat/firestore';
import { Observable } from 'rxjs';

export interface Item { name: string; }

@Component({
  selector: 'app-root',
  template: `
    <ul>
      <li *ngFor="let item of items | async">
        {{ item.name }}
      </li>
    </ul>
  `
})
export class AppComponent {
  private itemsCollection: AngularFirestoreCollection<Item>;
  items: Observable<Item[]>;
  constructor(private afs: AngularFirestore) {
    this.itemsCollection = afs.collection<Item>('items');
    this.items = this.itemsCollection.valueChanges();
  }
  addItem(item: Item) {
    this.itemsCollection.add(item);
  }
}
